def main():
    total_sales = 0
    
    # Add sales for each month
    january_sales = 5000  # Example sales amount for January
    february_sales = 6000  # Example sales amount for February
    march_sales = 7000  # Example sales amount for March
    
    total_sales += january_sales
    total_sales += february_sales
    total_sales += march_sales
    
    # Print the total sales to the screen
    print("Total Sales:", total_sales)

if __name__ == "__main__":
    main()
